<?phpclass Continente extends Modelo{    public $nombre_tabla = 'continente';    public $pk = 'id_continente';            public $atributos = array(        'id_continente'=>array(),		'nombre'=>array(),    );        public $errores = array( );        private $id_continente;	private $nombre;               function Continente(){        parent::Modelo();    }			//Inicio get atributos	public function get_atributos(){        $rs = array();        foreach ($this->atributos as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }    	//Inicio de id_continente    public function get_id_continente(){        $rs = array();        foreach ($this->id_continente as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_id_continente(){        return $this->id_continente;    }     public function set_id_continente($valor){        $er = new Er();                if ( !$er->valida_id_continente($valor) ){            $this->errores[] = "Esta id_continente (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where id_continente = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de ciudad (".$valor.") ya esta registrado";         }else{            $this->id_continente = trim($valor);        }    }	//Fin de id_ciudad	//Inicio de nombre	public function get_Nombre(){        $rs = array();        foreach ($this->nombre as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_Nombre(){        return $this->sube;    }    public function set_Nombre($valor){        $er = new Er();                if ( !$er->valida_Nombre($valor) ){            $this->errores[] = "Este nombre (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este nombre (".$valor.") ya esta registrado";         }else{            $this->nombre = trim($valor);        }    }	//Fin de nombre	}//Fin de clase Ciudad?>