<?phpclass Estadio extends Modelo{    public $nombre_tabla = 'estadio';    public $pk = 'id_estadio';            public $atributos = array(        'id_estadio'=>array(),		'Nombre'=>array(),		'id_ciudad'=>array(),    );        public $errores = array( );        private $id_estadio;	private $Nombre;    private $id_ciudad;        function Estadio(){        parent::Modelo();    }    	//Inicio de id_estadio    public function get_id_estadio(){        $rs = array();        foreach ($this->id_estadio as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_id_estadio(){        return $this->id_estadio;    } 		//Inicio get atributos	public function get_atributos(){        $rs = array();        foreach ($this->atributos as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }    public function set_id_estadio($valor){        $er = new Er();                if ( !$er->valida_id_estadio($valor) ){            $this->errores[] = "Esta id estadio (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where id_continente = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id estadio (".$valor.") ya esta registrado";         }else{            $this->id_estadio = trim($valor);        }    }	//Fin de id_estadio	//Inicio de Nombre	public function get_Nombre(){        $rs = array();        foreach ($this->Nombre as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_Nombre(){        return $this->sube;    }    public function set_Nombre($valor){        $er = new Er();                if ( !$er->valida_Nombre($valor) ){            $this->errores[] = "Este Nombre (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where Nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este Nombre (".$valor.") ya esta registrado";         }else{            $this->Nombre = trim($valor);        }    }	//Fin de Nombre	}//Fin de clase Estadio?>