<?phpclass Estadio extends Modelo{    public $nombre_tabla = 'estadio';    public $pk = 'idestadio';            public $atributos = array(        'idestadio'=>array(),		'Nombre'=>array(),		'idciudad'=>array(),    );        public $errores = array( );        private $idestadio;	private $Nombre;    private $idciudad;        function Estadio(){        parent::Modelo();    }		//Inicio de idestadio    public function get_idestadio(){        return $this->idestadio;    }     public function set_idestadio($valor){        $er = new Er();                if ( !$er->valida_idestadio($valor) ){            $this->errores[] = "Esta idestadio (".$valor.") no es valido";        }            $this->idestadio = trim($valor);    }//Fin de ididestadio		//Inicio nombre    public function get_Nombre(){        return $this->Nombre;    }     public function set_Nombre($valor){        $er = new Er();                if ( !$er->valida_Nombre($valor) ){            $this->errores[] = "Esta idestadio (".$valor.") no es valido";        }            $this->Nombre = trim($valor);    }//Fin nombre		//Inicio de id ciudad    public function get_idciudad(){        return $this->idciudad;    }     public function set_idciudad($valor){        $er = new Er();                if ( !$er->valida_idciudad($valor) ){            $this->errores[] = "Esta idciudad (".$valor.") no es valido";        }            $this->idciudad = trim($valor);    }//Fin de id ciudad		}//Fin de clase Estadio?>