<?phpclass Integrante extends Modelo{    public $nombre_tabla = 'integrante';    public $pk = 'idintegrante';            public $atributos = array(        'idintegrante'=>array(),		'nombre'=>array(),		'apellido'=>array(),		'peso'=>array(),		'estatura'=>array(),		'foto'=>array(),		'Edad'=>array(),		'idequipo'=>array(),    );        public $errores = array( );        private $idintegrante;	private $nombre;	private $apellido;	private $peso;	private $estatura;	private $foto;	private $Edad;	private $idequipo;               function Integrante(){        parent::Modelo();    }			//Inicio de nombre    public function get_nombre(){        return $this->nombre;    }	    public function set_nombre($valor){        $er = new Er();                if ( !$er->valida_nombre($valor) ){            $this->errores[] = "Este nombre (".$valor.") no es valido";        }            $this->nombre = trim($valor);        }//Fin de nombre			//Inicio de apellido    public function get_apellido(){        return $this->apellido;    }     public function set_apellido($valor){        $er = new Er();                if ( !$er->valida_apellido($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->apellido = trim($valor);    }//Fin de apellido		//Inicio de peso        public function get_peso(){        return $this->peso;    }     public function set_peso($valor){        $er = new Er();                if ( !$er->valida_peso($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->peso = trim($valor);    }//Fin de peso		//Inicio de estatura    public function get_estatura(){        return $this->estatura;    }     public function set_estatura($valor){        $er = new Er();                if ( !$er->valida_estatura($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->estatura = trim($valor);        }//Fin de estatura		//Inicio de foto    public function get_foto(){        return $this->foto;    }     public function set_foto($valor){        $er = new Er();                if ( !$er->valida_foto($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->foto = trim($valor);    }//Fin de foto		//Inicio de Edad    public function get_Edad(){        return $this->Edad;    }     public function set_Edad($valor){        $er = new Er();                if ( !$er->valida_Edad($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->Edad = trim($valor);    }//Fin de Edad		//Inicio de idequipo    public function get_idequipo(){        return $this->idequipo;    }     public function set_idequipo($valor){        $er = new Er();                if ( !$er->valida_idequipo($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->idequipo = trim($valor);    }//Fin de idequipo	}//Fin de clase Integrante?>