iudad (".$valor.") ya esta registrado";         }else{            $this->peso = trim($valor);        }    }//Fin de peso		//Inicio de estatura    public function get_estatura(){        $rs = array();        foreach ($this->estatura as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_estatura(){        return $this->sube;    }     public function set_estatura($valor){        $er = new Er();                if ( !$er->valida_estatura($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de ciudad (".$valor.") ya esta registrado";         }else{            $this->estatura = trim($valor);        }    }//Fin de estatura		//Inicio de foto    public function get_foto(){        $rs = array();        foreach ($this->foto as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_foto(){        return $this->sube;    }     public function set_foto($valor){        $er = new Er();                if ( !$er->valida_foto($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de ciudad (".$valor.") ya esta registrado";         }else{            $this->foto = trim($valor);        }    }//Fin de foto		//Inicio de Edad    public function get_Edad(){        $rs = array();        foreach ($this->Edad as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_Edad(){        return $