<?phpclass Pais extends Modelo{    public $nombre_tabla = 'pais';    public $pk = 'id_pais';            public $atributos = array(        'id_pais'=>array(),		'nombre'=>array(),		'bandera'=>array(),		'id_continente'=>array(),    );        public $errores = array( );        private $id_pais;	private $nombre;	private $bandera;	private $id_continente;               function Pais(){        parent::Modelo();    }	//Inicio de nombre    public function get_nombre(){        $rs = array();        foreach ($this->nombre as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_nombre(){        return $this->sube;    }     public function set_nombre($valor){        $er = new Er();                if ( !$er->valida_nombre($valor) ){            $this->errores[] = "Esta nombre (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este nombre (".$valor.") ya esta registrado";         }else{            $this->nombre = trim($valor);        }    }//Fin de nombre		//Inicio de bandera    public function get_bandera(){        $rs = array();        foreach ($this->bandera as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_bandera(){        return $this->sube;    }     public function set_bandera($valor){        $er = new Er();                if ( !$er->valida_bandera($valor) ){            $this->errores[] = "Esta bandera (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where bandera = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Esta bandera (".$valor.") ya esta registrado";         }else{            $this->bandera = trim($valor);        }    }//Fin de bandera		//Inicio de id_continente    public function get_id_continente(){        $rs = array();        foreach ($this->id_continente as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_id_continente(){        return $this->sube;    }     public function set_id_continente($valor){        $er = new Er();                if ( !$er->valida_id_continente($valor) ){            $this->errores[] = "Esta id de continente (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where id_continente = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de continente (".$valor.") ya esta registrado";         }else{            $this->id_continente = trim($valor);        }    }//Fin de id_continente			    }//Fin de clase Pais?>