<?phpclass Posision extends Modelo{    public $abreviatura_tabla = 'posision';    public $pk = 'id_posision';            public $atributos = array(        'id_posision'=>array(),		'nombre'=>array(),		'abreviatura'=>array(),    );        public $errores = array( );        private $id_posision;	private $nombre;	private $abreviatura;               function Posision(){        parent::Modelo();    }		//Inicio get atributos	public function get_atributos(){        $rs = array();        foreach ($this->atributos as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }		//Inicio de id_posision    public function get_id_posision(){        $rs = array();        foreach ($this->id_posision as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_id_posision(){        return $this->id_posision;    }     public function set_id_posision($valor){        $er = new Er();                if ( !$er->valida_id_posision($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where id_posision = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de ciudad (".$valor.") ya esta registrado";         }else{            $this->id_posision = trim($valor);        }    }//Fin de id_posision		//Inicio de nombre    public function get_nombre(){        $rs = array();        foreach ($this->nombre as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_nombre(){        return $this->sube;    }     public function set_nombre($valor){        $er = new Er();                if ( !$er->valida_nombre($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de ciudad (".$valor.") ya esta registrado";         }else{            $this->nombre = trim($valor);        }    }//Fin de nombre		//Inicio de abreviatura    public function get_abreviatura(){        $rs = array();        foreach ($this->abreviatura as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }        public function get_abreviatura(){        return $this->sube;    }     public function set_abreviatura($valor){        $er = new Er();                if ( !$er->valida_abreviatura($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from usuarios where abreviatura = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este id de ciudad (".$valor.") ya esta registrado";         }else{            $this->abreviatura = trim($valor);        }    }//Fin de abreviatura			    }//Fin de posision