<?phpclass Posision extends Modelo{    public $abreviatura_tabla = 'posision';    public $pk = 'idposision';            public $atributos = array(        'idposision'=>array(),		'nombre'=>array(),		'abreviatura'=>array(),    );        public $errores = array( );        private $idposision;	private $nombre;	private $abreviatura;               function Posision(){        parent::Modelo();    }		//Inicio de idposision    public function get_idposision(){        return $this->idposision;    }     public function set_idposision($valor){        $er = new Er();                if ( !$er->valida_idposision($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->idposision = trim($valor);    }//Fin de idposision		//Inicio de nombre    public function get_nombre(){        return $this->nombre;    }     public function set_nombre($valor){        $er = new Er();                if ( !$er->valida_nombre($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->nombre = trim($valor);    }//Fin de nombre		//Inicio de abreviatura    public function get_abreviatura(){        return $this->abreviatura;    }     public function set_abreviatura($valor){        $er = new Er();                if ( !$er->valida_abreviatura($valor) ){            $this->errores[] = "Esta id de ciudad (".$valor.") no es valido";        }            $this->abreviatura = trim($valor);    }//Fin de abreviatura}//Fin de posision