<?php   session_start();  include ('../layouts/header.php');?><?phpclass pais extends Modelo{    public $nombre_tabla = 'pais';    public $pk = 'id_pais';            public $atributos = array(        'Nombre'=>array(),        'bandera'=>array(),    );        public $errores = array( );        private $Nombre;    private $bandera;               function pais(){        parent::Modelo();    }        public function get_atributos(){        $rs = array();        foreach ($this->atributos as $key => $value) {            $rs[$key]=$this->$key;        }        return $rs;    }            public function get_Nombre(){        return $this->sube;    }     public function set_Nombre($valor){        $er = new Er();                if ( !$er->valida_Nombre($valor) ){            $this->errores[] = "Este e-mail (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from pais where Nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Este e-mail (".$valor.") ya esta registrado";         }else{            $this->Nombre = trim($valor);        }    }    public function get_bandera(){        return $this->baja;    }        public function set_bandera($valor){        $er = new Er();                if ( !$er->valida_bandera($valor) ){            $this->errores[] = "Esta bandera (".$valor.") no es valido";        }        $rs = $this->consulta_sql("select * from pais where Nombre = '$valor'");        $rows = $rs->GetArray();                if(count($rows) > 0){            $this->errores[] = "Esta bandera (".$valor.") ya esta registrado";         }else{            $this->bandera = trim($valor);        }    }}?><?php include ('../layouts/footer.php'); ?>